import React, { useState } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';

function AdminGenerateLink() {
  const [adhaar, setAdhaar] = useState('');
  const [registrationToken, setRegistrationToken] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleGenerateLink = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/admin/generate-link', { adhaar });
      setRegistrationToken(response.data.registrationToken);
      setErrorMessage('');
      Navigate("/dashboard")
    } catch (error) {
      setRegistrationToken('');
      setErrorMessage(error.response.data.message);
    }
  };

  return (
    <div>
      <h1>Generate Registration Link</h1>
      <div>
        <label>User ID:</label>
        <input
          type="text"
          value={adhaar}
          onChange={(e) => setAdhaar(e.target.value)}
        />
      </div>
      <button onClick={handleGenerateLink}>Generate Link</button>
      {registrationToken && (
        <div>
          <p>Generated Registration Token: {registrationToken}</p>
        </div>
      )}
      {errorMessage && (
        <div>
          <p>Error: {errorMessage}</p>
        </div>
      )}
    </div>
  );
}

export default AdminGenerateLink;
