import React, { useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData , setFormData] = useState({
    email:"",
    password:"",
    name:"",
    address:"",
    profile:"",
    phone:""
  })
  const navigate = useNavigate()
  const {email,password,name,address,profile,phone} = formData

  const onChange =(e)=>{
    setFormData({...formData , [e.target.name]:e.target.value})
  }
  const handleRegister = async (e) => {
    e.preventDefault()
    const newUser ={
      email,
      password,
      name,
      address,
      profile,
      phone
    }
    try {
      const res = await axios.post("", newUser)
      console.log(res.data)
      navigate("/")
    } catch (err) {
      console.error(err.response.data)

    }
  }

  return (
    <div>


      <h2>Register</h2>
      <TextField
  id="outlined-controlled"
  label="Email"
  value={email}
  onChange={onChange}
/>
<TextField
  id="outlined-controlled"
  label="Password"
  value={password}
  onChange={onChange}
/>
<TextField
  id="outlined-controlled"
  label="Name"
  value={name}
  onChange={onChange}
/>
<TextField
  id="outlined-controlled"
  label="Address"
  value={address}
  onChange={onChange}
/>
<TextField
  id="outlined-controlled"
  label="Phone"
  value={phone}

/>


<Button  onClick ={handleRegister}variant="contained">Register</Button>





    </div>
  );
};

export default Register;
