import React, { useState } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import {Link} from 'react-router-dom'
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Container from '@mui/material/Container';
import { Card, CardContent, CardActions } from "@mui/material";
import Input from "@mui/material/Input";
import Grid from "@mui/material/Grid";
const Register = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    name: "",
    address: "",
    profile: null,
    phone: "",
  });
  const navigate = useNavigate();
  const { email, password, name, address, profile, phone } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  const handleFileChange = (e) => {
    setFormData({ ...formData, profile: e.target.files[0] });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const newUser = {
      email,
      password,
      name,
      address,
      profile,
      phone,
    };
    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/register",
        newUser
      );
      console.log(res.data);
      navigate("/");
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <Container maxWidth="md">
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: "100vh" }}
      >
        <Card  style={{ width: '70%', textAlign: 'center' }}>
          <CardContent>
            <Grid container direction="column" spacing={2}>
              <Grid item>
                <h2>Register</h2>
              </Grid>
              <Grid item>
                <TextField
                  id="outlined-controlled"
                  name="email"
                  label="Email"
                  value={email}
                  onChange={onChange}
                  fullWidth
                />
              </Grid>
              <Grid item>
                <TextField
                   label="Password"
                   name="password"
                   value={password}
                   onChange={onChange}
                   variant="outlined"
                   type="password"
                   fullWidth
                />
              </Grid>
              <Grid item>
                <TextField
                  id="outlined-controlled"
                  name="name"
                  label="Name"
                  value={name}
                  onChange={onChange}
                  fullWidth
                />
              </Grid>
              <Grid item>
                <TextField
                  id="outlined-controlled"
                  name="address"
                  label="Address"
                  value={address}
                  onChange={onChange}
                  fullWidth
                />
              </Grid>
              <Input
               id="outlined-controlled"
                  type="file"
                  name="profile"
                  onChange={handleFileChange}
                  fullWidth
                />
              <Grid item>
                <TextField
                  id="outlined-controlled"
                  name="phone"
                  label="Phone"
                  value={phone}
                  onChange={onChange}
                  fullWidth
                />
              </Grid>
            </Grid>
          </CardContent>
          <CardActions  style={{ justifyContent: 'center' }}>
            <Button onClick={handleRegister} variant="contained" color="primary" fullWidth>
              Register
            </Button>
          </CardActions>
          < p>Do you have an account? <Link to = '/login'>Login</Link> here</p>
        </Card>
      </Grid>

    </Container>
  );
};

export default Register;
