import React, { useState } from 'react';
import { Card, CardContent, TextField, Button, Typography , Grid , Box} from '@mui/material';
import HypervergeLogo from './Hyperverge logo.png'

import axios from 'axios'; 

function AdminGenerateLink() {
  const [temporaryToken, setTemporaryToken] = useState('');
  const [registrationLink, setRegistrationLink] = useState('');

  useEffect(() => {
    async function generateLink() {
      try {
        const response = await axios.post('http://localhost:5000/api/admin/generate-link');
        setTemporaryToken(response.data.temporaryToken);
        setRegistrationLink(`/register?tempToken=${response.data.temporaryToken}`);
      } catch (error) {
        console.error(error);
      }
    }

    generateLink();
  }, []);



  return (
    <Grid container justifyContent="center" alignItems="center" height="100vh">
    <Grid item xs={12} md={6}>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        height="100%"
      >
                  <div>
            <img
              src={HypervergeLogo}
              alt="Hyperverge Logo"
              style={{ width: "500px", marginRight:"10px" }}
            />
          </div>


      </Box>
    </Grid>
    <Grid item xs={12} md={6}>
      <Grid
        container
        spacing={2}
        justifyContent="center"
        alignItems="center"
        height="100%"
      >
        <Grid item>
            <Card>
              <CardContent>
                <Typography variant="h5">Generate Registration Link</Typography>
                <TextField
                  label="User ID"
                  variant="outlined"
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  fullWidth
                  margin="normal"
                />
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleGenerateLink}
                >
                  Generate Link
                </Button>
                {registrationToken && (
                  <div>
                    <Typography variant="body1" color="textPrimary">
                      Generated Registration Token: {registrationToken}
                    </Typography>
                  </div>
                )}
                {errorMessage && (
                  <div>
                    <Typography variant="body1" color="error">
                      Error: {errorMessage}
                    </Typography>
                  </div>
                )}
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Grid>
    </Grid>











  );
}

export default AdminGenerateLink;
