import React, { useState, useEffect } from 'react';
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import axios from "axios";
import Container from "@mui/material/Container";
import Box from "@mui/material/Box";
import CircularProgress from "@mui/material/CircularProgress";
import Button from "@mui/material/Button";

function Dashboard() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [editedUser, setEditedUser] = useState({
    name: "",
    email: "",
    address: "",
    phone: "",
  });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/user/all");
      setUsers(response.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  const handleUpdateUser = async (id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${id}`);
      setSelectedUser(response.data);
      setEditedUser(response.data); // Set the editedUser state for editing
    } catch (err) {
      console.error(err);
    }
  };

  const handleSaveEditedUser = async () => {
    try {
      await axios.put(`http://localhost:5000/api/user/${selectedUser._id}`, editedUser);
      fetchUsers();
      setSelectedUser(null); // Clear the selected user
      setEditedUser({
        name: "",
        email: "",
        address: "",
        phone: "",
      }); // Clear the editedUser data
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Box mt={4}>
        <Grid container spacing={4} justifyContent="center">
          {loading ? (
            <CircularProgress />
          ) : (
            users.map((user) => (
              <Grid key={user._id} item xs={12} sm={6} md={4} lg={3}>
                <Card elevation={3}>
                  <CardContent>
                    <Typography variant="h6" align="center" gutterBottom>
                      {user.name}
                    </Typography>
                    <Typography color="textSecondary" align="center">
                      Email: {user.email}
                    </Typography>
                    <Typography color="textSecondary" align="center">
                      Address: {user.address}
                    </Typography>
                    <Typography color="textSecondary" align="center">
                      Phone: {user.phone}
                    </Typography>
                    <Box mt={2} textAlign="center">
                      <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => handleUpdateUser(user._id)}
                      >
                        Update
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))
          )}
        </Grid>
      </Box>
      {selectedUser && (
        <Card>
          <CardContent>
          <EditForm
              editedUser={editedUser}
              handleSaveEditedUser={handleSaveEditedUser}
              setEditedUser={setEditedUser}
            />

            <form>
              <label>Name:</label>
              <input
                type="text"
                value={editedUser.name}
                onChange={(e) => setEditedUser({ ...editedUser, name: e.target.value })}
              />
              <label>Email:</label>
              <input
                type="email"
                value={editedUser.email}
                onChange={(e) => setEditedUser({ ...editedUser, email: e.target.value })}
              />
              <label>Address:</label>
              <input
                type="text"
                value={editedUser.address}
                onChange={(e) => setEditedUser({ ...editedUser, address: e.target.value })}
              />
              <label>Phone:</label>
              <input
                type="text"
                value={editedUser.phone}
                onChange={(e) => setEditedUser({ ...editedUser, phone: e.target.value })}
              />
              <Button variant="contained" color="primary" onClick={handleSaveEditedUser}>
                Save
              </Button>
            </form>
          </CardContent>
        </Card>
      )}
    </Container>
  );
}

export default Dashboard;
