// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes,  Route } from 'react-router-dom';
// import axios from 'axios';
import Login from './Component/UserLogin';
import Dashboard from './Component/userDashboard';
import Register from './Component/UserRegister';

const App = () => {
  const [token, setToken] = useState('');

  // Check if the token exists in localStorage on initial load
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setToken(storedToken);
    }
  }, []);

  // Save the token to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('token', token);
  }, [token]);

  return (
    <Router>
      <Routes>
        <Route exact path="/login" render={() => (token ? <Redirect to="/dashboard" /> : <Login setToken={setToken} />)} />
        <Route exact path="/register" render={() => (token ? <Redirect to="/dashboard" /> : <Register />)} />
        <Route exact path="/dashboard" render={() => (token ? <Dashboard /> : <Redirect to="/login" />)} />
        <Route path="*" render={() => <Redirect to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
