// components/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import Avatar from "@mui/material/Avatar";
import TextField from "@mui/material/TextField";

const UserDashboard = ({ token, userId }) => {
  const [formData, setFormData] = useState({});
  console.log("formData", formData)

  useEffect(() => {
    const fetchUserData = async () => {
      if (userId) { // Check if userId is defined before making the API request
        try {
          const response = await axios.get(
            `http://localhost:5000/api/user/${userId}`,
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          const user = response.data;
          setFormData(user);
          setLoading(false);
        } catch (error) {
          console.error(error);
        }
      }
    };

    fetchUserData();
  }, [userId, token]);



  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const updateResponse = await axios.put(
        `http://localhost:5000/api/user/${userId}`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const updatedUser = updateResponse.data;
      console.log("Data updated successfully!", updatedUser);
      setFormData(updatedUser);
    } catch (error) {
      console.error(error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div>
      <h2>Welcome to Your Dashboard</h2>
      <Avatar
        src={formData.profile}
        alt={formData.name}
        sx={{ width: 156, height: 156 }}
      />

      <TextField
        id="outlined-controlled-email"
        name="email"
        label="Email"
        value={formData.email || ""}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-password"
        name="password"
        label="Password"
        value={formData.password || ""}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-name"
        name="name"
        label="Name"
        value={formData.name || ""}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-address"
        name="address"
        label="Address"
        value={formData.address || ""}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-phone"
        name="phone"
        label="Phone"
        value={formData.phone || ""}
        onChange={handleInputChange}
        fullWidth
      />
      <button onClick={handleSubmit}>Update</button>
    </div>
  );
};

export default UserDashboard;
