import React, { useState , useEffect } from "react";
import {useParams} from 'react-router-dom'
import TextField from "@mui/material/TextField";
import axios from 'axios'


const ImageUpload = ({ folderName }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [fileId, setFileId] = useState(null);
  const { id } = useParams();
  const [userData, setUserData] = useState(null);
  const [updatedName, setUpdatedName] = useState('');
  const [updatedEmail, setUpdatedEmail] = useState('');
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`/api/${id}`);
        setUserData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchUserData();
  }, [id]);

  const handleUpdate = async () => {
    try {
      const response = await axios.put(`/api/${id}`, {
        updatedName,
        updatedEmail,
      });
      setUserData(response.data);
    } catch (error) {
      console.error(error);
    }
  };


  const handleImageUpload = (event) => {
    setSelectedImage(event.target.files[0]);
  };
  const handleClick = () => {
    console.log("clicked");
    if (!selectedImage) {
      return;
    }
    const data = new FormData();
    console.log(data);
    console.log("namee", folderName);
    data.append("file", selectedImage);

    data.append("upload_preset", "Image_upload");
    data.append("cloud_name", "dvib3dpad");
    data.append("folder", folderName);

    fetch("https://api.cloudinary.com/v1_1/dvib3dpad/image/upload", {
      method: "post",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setImageUrl(data.secure_url);
        setFileId(data.fileId);

        console.log(data.secure_url);
      })
      .catch((err) => {
        console.log("error");
      });
  };
  const onChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };


  const isUserAuthenticated = true;

  return (
    <>

      <div className="container">
        <h1 className="text-center">Add a post</h1>
        <div className="row justify-content-center mt-5">
          <div className="col-md-6">
            <div className="card">
              <div className="card-body">
                <input
                  type="file"
                  className="form-control mb-3"
                  onChange={handleImageUpload}
                />
                {isUserAuthenticated ? (
                  <>
                    <button
                      className="btn btn-primary"
                      disabled={!selectedImage}
                      onClick={handleClick}
                    >
                      Upload Image
                    </button>

                    {imageUrl && (
                      <div className="mt-4">
                        <h2>Uploaded Image:</h2>
                        <img
                          src={imageUrl}
                          alt="Uploaded"
                          className="img-fluid"
                        />
                      </div>
                    )}
                  </>
                ) : (
                  <p>You must be authenticated to view the image.</p>
                )}{" "}
                <br />
              </div>
            </div>
          </div>
        </div>
      </div>
      <TextField
      label="Email"
      value={userData.email}
      onChange={onChange}
      variant="outlined"
    />
    </>

  );
};

export default ImageUpload;
