import React, { useState } from 'react';
import axios from 'axios';
import { Card, CardContent, TextField, Button, Typography, Grid } from '@mui/material';

import { useNavigate } from 'react-router-dom';

function AdminGenerateLink() {
  const [adhaar, setAdhaar] = useState('');
  const [registrationToken, setRegistrationToken] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleGenerateLink = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/admin/generate-link', { adhaar });
      console.log("response",response)
      setRegistrationToken(response.data.registrationToken);
      setErrorMessage('');
      navigate('/');
    } catch (error) {
      setRegistrationToken('');
      setErrorMessage(error.message);
    }
  };

  return (
    <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
      <Grid item xs={12} sm={8} md={6} lg={4}>
        <Card>
          <CardContent>
            <Typography variant="h5" align="center">
              Generate Registration Link
            </Typography>
            <TextField
              label="Adhaar Number"
              variant="outlined"
              fullWidth
              value={adhaar}
              onChange={(e) => setAdhaar(e.target.value)}
              margin="normal"
            />
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={handleGenerateLink}
            >
              Generate Link
            </Button>
            {registrationToken && (
              <Typography variant="body1" color="textPrimary" align="center">
                Generated Registration Token: {registrationToken}
              </Typography>
            )}
            {errorMessage && (
              <Typography variant="body1" color="error" align="center">
                Error: {errorMessage}
              </Typography>
            )}

            {/* Display registration token */}
            {registrationToken && (
             <Typography variant="body1" color="textPrimary" align="center">
             Generated Registration Token: {registrationToken}
           </Typography>
            )}
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}

export default AdminGenerateLink;
