import React, { useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import {Card, CardContent, CardActions } from '@mui/material';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import axios from 'axios';
import {  useNavigate } from 'react-router-dom';

const Login = ({setToken, setUserId}) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate()

  const handleLogin = async () => {
    console.log("hgjhkjhjk")
    try {
      const response = await axios.post('http://localhost:5000/api/user/login', { email, password });
      console.log("res", response)
      if (response.data ) {
        console.log("response", response.data);
        const { token , userId } = response.data;

        setToken(token)
        // setUserId(userId)
        navigate('/dashboard')
      } else {
        console.error("Unexpected response format:", response.data);
      }
    } catch (error) {
      if (error.response) {
        console.error(error.response.data.error);
      } else {
        console.error("Error during request:", error.message);
      };
    }
  };

  return (
    <Container maxWidth="sm"> {/* Adjust maxWidth to your preference */}
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Card style={{ width: '100%', maxWidth: '400px' }}> {/* Adjust maxWidth for the Card */}
          <CardContent>
            <Grid container direction="column" alignItems="center" spacing={2}>
              <Grid item>
                <h2>Login</h2>
                {/* You can add a subtitle or any other text here */}
              </Grid>
              <Grid item>
                <TextField
                  label="Email"
                  value={email}
                  onChange={(event) => setEmail(event.target.value)}
                  variant="outlined"
                />
              </Grid>
              <Grid item>
                <TextField
                  label="Password"
                  value={password}
                  onChange={(event) => setPassword(event.target.value)}
                  variant="outlined"
                  type="password"
                />
              </Grid>
            </Grid>
          </CardContent>
          <CardActions style={{ justifyContent: 'center', paddingBottom: '16px' }}>
            <Button onClick={handleLogin} variant="contained" color="primary">
              Login
            </Button>
          </CardActions>
        </Card>
      </Grid>
    </Container>
  );
};

export default Login;
