import React, { useEffect, useState } from 'react';
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import axios from 'axios';
import { Avatar } from '@mui/material';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  // const [inviteLink, setInviteLink] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/user/all');
      setUsers(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      fetchUsers();
    } catch (err) {
      console.error(err);
    }
  };

  // const generateInviteLink = async () => {
  //   try {
  //     const response = await axios.post('http://localhost:5000/api/invite');
  //     setInviteLink(response.data.inviteLink);
  //   } catch (err) {
  //     console.error(err);
  //   }
  // };

  return (
    <Grid container spacing={2}>
    {users.map((user) => (
      <Grid key={user._id} item xs={12} sm={6} md={4} lg={3}>
        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              {user.name}
            </Typography>
            <Avatar src={user.profile} ></Avatar>
            <Typography color="textSecondary">Email: {user.email}</Typography>
            <Typography color="textSecondary">Address: {user.address}</Typography>
            <Typography color="textSecondary">Address: {user.phone}</Typography>
            {/* Include other user details here */}
          </CardContent>
        </Card>
      </Grid>
    ))}
  </Grid>
  );
};

export default AdminDashboard;
