import React, { useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import {Card, CardContent, CardActions } from '@mui/material';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import axios from 'axios';

const Login = ({setToken}) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/user/login', { email, password });
      const { token }  = response.data
      console.log(token)
      setToken(token)
      // Save the token to localStorage or Redux store for further authentication
    } catch (error) {
      console.error(error.response.data.error);
    }
  };

  return (
    <Container maxWidth="sm"> {/* Adjust maxWidth to your preference */}
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Card style={{ width: '100%', maxWidth: '400px' }}> {/* Adjust maxWidth for the Card */}
          <CardContent>
            <Grid container direction="column" alignItems="center" spacing={2}>
              <Grid item>
                <h2>Login</h2>
                {/* You can add a subtitle or any other text here */}
              </Grid>
              <Grid item>
                <TextField
                  label="Email"
                  value={email}
                  onChange={(event) => setEmail(event.target.value)}
                  variant="outlined"
                />
              </Grid>
              <Grid item>
                <TextField
                  label="Password"
                  value={password}
                  onChange={(event) => setPassword(event.target.value)}
                  variant="outlined"
                  type="password"
                />
              </Grid>
            </Grid>
          </CardContent>
          <CardActions>
            <Button onClick={handleLogin} variant="contained" color="primary">
              Login
            </Button>
          </CardActions>
        </Card>
      </Grid>
    </Container>
  );
};

export default Login;
