// App.js
// import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import axios from 'axios';
import './App.css';

const App = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');

  const handleLogin = async () => {
    try {
      const response = await axios.post('/api/user/login', { email, password });
      const { token } = response.data;
      setToken(token);
      localStorage.setItem('token', token);
    } catch (error) {
      console.error(error);
    }
  };

  const handleLogout = () => {
    setToken('');
    localStorage.removeItem('token');
  };

  const handleRegister = async () => {
    try {
      await axios.post('/api/user/register', { email, password });
      console.log('User registered successfully');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Router>
      <div className="App">
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            {!token ? (
              <>
                <li>
                  <input type="text" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
                </li>
                <li>
                  <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </li>
                <li>
                  <button onClick={handleLogin}>Login</button>
                </li>
                <li>
                  <button onClick={handleRegister}>Register</button>
                </li>
              </>
            ) : (
              <li>
                <button onClick={handleLogout}>Logout</button>
              </li>
            )}
          </ul>
        </nav>

        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/dashboard">
            {token ? <Dashboard /> : <div>Please login to access the dashboard.</div>}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

const Home = () => {
  return <div>Welcome to the MERN Authentication App!</div>;
};

const Dashboard = () => {
  return <div>User Dashboard - Add your user-specific content here.</div>;
};

export default App;
