// components/Dashboard.js
import React ,{useState, useEffect} from 'react';
import axios from 'axios'
import TextField from "@mui/material/TextField";


const UserDashboard = ({token ,_id}) => {
  const [formData, setFormData] = useState({});
  console.log("formate",formData)
  useEffect(() => {
    const fetchUserData = async () => {
      // console.log("user", userId)
      try {


        const response = await axios.get(`http://localhost:5000/api/user/${user._id}`);

        const user = response.data;

        setFormData(user);
      } catch (error) {
        console.error(error);
      }
    };

    fetchUserData();
  }, [_id]);
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {

      const updateResponse = await axios.put(
        `http://localhost:5000/api/user/${user._id}`,
        formData
      );
      const data = updateResponse.data;
      console.log("fecthing the editData", data);
      // Perform any necessary actions after the update is successful
      console.log("updated" ,)
      console.log("Data updated successfully!");
    } catch (error) {
      console.error(error);
      // setError(error.message);
    }
  };


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div>

      <h2>Welcome to Your Dashboard</h2>
      <TextField
        id="outlined-controlled-email"
        name="email"
        label="Email"
        value={formData.email || ''}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-password"
        name="password"
        label="Password"
        value={formData.password || ''}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-name"
        name="name"
        label="Name"
        value={formData.name || ''}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-address"
        name="address"
        label="Address"
        value={formData.address || ''}
        onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-phone"
        name="phone"
        label="Phone"
        value={formData.phone || ''}
        onChange={handleInputChange}
        fullWidth
      />
<button onClick={handleSubmit}>Update</button>



</div>
) 

};

export default UserDashboard;
