import React, { useState , useEffect } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import { Link } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Snackbar from "@mui/material/Snackbar";
import MuiAlert from "@mui/material/Alert";
import { Card, CardContent, CardActions } from "@mui/material";
import { v4 as uuidv4 } from "uuid"; 

import Grid from "@mui/material/Grid";
import HypervergeLogo from "./Hyperverge logo.png";

const Register = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    name: "",
    address: "",
    profile: "",
    phone: "",
    registrationToken:""

  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [openSnackbar, setOpenSnackbar] = useState(false);
  // const [inviteLink, setInviteLink] = useState("");


  const navigate = useNavigate();
  const { email, password, name, address, profile, phone,registrationToken} =
    formData;

    useEffect(() => {
      if (!registrationToken) {
        generateInviteLink(); // Automatically generate an invite link when the component loads
      }
    }, []);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!registrationToken) {
      setError("Invalid invite link. Please contact your Admin.");
      setSuccess("");
      setOpenSnackbar(true);
      return;
    }


    const newUser = {
      email,
      password,
      name,
      address,
      profile,
      phone,
      registrationToken,
    };

    try {
      const res = await axios.post(
        "http://localhost:5000/api/user/register",
        newUser
      );
      console.log(res.data);
      setError("");
      setSuccess("Registration successful!");
      setOpenSnackbar(true);
      navigate("/dashboard");
    } catch (err) {
      setError("Invalid register credentials");
      setSuccess("");
      setOpenSnackbar(true);
      console.error(err.response.data);
    }
  };
  const generateInviteLink = async () => {
    try {
      // Generate a unique invite link using UUID
      const inviteLink = uuidv4();

      // Call the backend API to save the invite link
      const response = await axios.post("/api/admin/generate-invite-link");
      const generatedLink = response.data.inviteLink;

      // Update the state with the generated invite link
      setFormData((prevData) => ({
        ...prevData,
        registrationToken: generatedLink,
      }));
    } catch (error) {
      console.error(error);
      setError("Error generating invite link");
      setOpenSnackbar(true);
    }
  };


  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <Container maxWidth="md">


      <Grid
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: "100vh" }}
      >
        <Grid
          item
          xs={12}
          md={6}
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div>
            <img
              src={HypervergeLogo}
              alt="Hyperverge Logo"
              style={{ width: "500px", paddingRight: "150px" }}
            />
          </div>
        </Grid>

        <Grid item xs={12} md={6}>
          <Card
            style={{ width: "100%", textAlign: "center", marginLeft: "180px" }}
          >
            <CardContent>
              <Grid container direction="column" spacing={2}>
                <Grid item>
                  <h2>Register</h2>

                  <br />

                  <TextField
                    id="outlined-controlled"
                    name="email"
                    label="Email"
                    value={email}
                    onChange={onChange}
                    fullWidth
                  />
                </Grid>
                <Grid item>
                  <TextField
                    label="Password"
                    name="password"
                    value={password}
                    onChange={onChange}
                    variant="outlined"
                    type="password"
                    fullWidth
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-controlled"
                    name="name"
                    label="Name"
                    value={name}
                    onChange={onChange}
                    fullWidth
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-controlled"
                    name="address"
                    label="Address"
                    value={address}
                    onChange={onChange}
                    fullWidth
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-controlled"
                    name="phone"
                    label="Phone"
                    value={phone}
                    onChange={onChange}
                    fullWidth
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-controlled"
                    name="registrationToken"
                    label="RegistationToken"
                    value={ registrationToken}
                    onChange={onChange}
                    fullWidth
                  />
                </Grid>
                <br /> <br />
              </Grid>
            </CardContent>
            <CardActions style={{ justifyContent: "center" }}>
              <Button
                onClick={handleRegister}
                variant="contained"
                color="primary"
                fullWidth
              >
                Register
              </Button>
            </CardActions>
            <p>
              Already have an account? <Link to="/login">Login</Link> here.
            </p>
          </Card>
        </Grid>
      </Grid>

      <Snackbar
        open={openSnackbar}
        autoHideDuration={3000}
        onClose={handleCloseSnackbar}
      >
        <MuiAlert
          elevation={6}
          variant="filled"
          onClose={handleCloseSnackbar}
          severity={error ? "error" : "success"}
        >
          {error || success}
        </MuiAlert>
      </Snackbar>
    </Container>
  );
};

export default Register;
