// components/Dashboard.js
import React ,{useState ,useEffect} from 'react';
import axios from 'axios'
// import {usePara , useNavigate} from 'react-router-dom'
// import User from '../../../backend/model/User';

const UserDashboard = ({token ,_id}) => {
  const [formData, setFormData]= useState({})
  // const {id } = useParams()
  console.log("", id)
  const navigate = useNavigate()
  useEffect(()=>{
    console.log("BOOK ID", id)
    axios.get(`http://localhost:5000/api/user/${formData_id}`)
    .then((res)=>{
      setFormData( res.data
      )
    })
    .catch((err)=>{
      console.log('Error from UpdateBookInfo')
    })
  },[id])
  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  const onSubmit = (e) => {
    e.preventDefault();

    const data = {
      email:formData.email,
      password: formData.password,
      address: formData.address,
      name: formData.name,
      profile: formData.profile,
      phone: formData.phone,
    };

    axios
      .put(`http://localhost:5000/api/user/${id}`, data)
      .then((res) => {
        // navigate(`/show-book/${id}`);
      })
      .catch((err) => {
        console.log('Error in UpdateBookInfo!');
      });
  };

  return (
    <div>
      <h2>Welcome to Your Dashboard</h2>
      <form noValidate  onSubmit={onSubmit}>
            <div className='form-group'>
              <label htmlFor='title'>Email</label>
              <input
                type='text'
                placeholder='Email'
                name='title'
                className='form-control'
                value={formData.email}
                onChange={onChange}
              />
            </div>
            <br />

            <div className='form-group'>
              <label htmlFor='isbn'>password</label>
              <input
                type='text'
                placeholder='password'
                name='isbn'
                className='form-control'
                value={formData.password}
                onChange={onChange}
              />
            </div>
            <br />

            <div className='form-group'>
              <label htmlFor='author'>address</label>
              <input
                type='text'
                placeholder='Address'
                name='author'
                className='form-control'
                value={formData.address}
                onChange={onChange}
              />
            </div>
            <br />

            <div className='form-group'>
              <label htmlFor='description'>name</label>
              <textarea
                type='text'
                placeholder='name'
                name='description'
                className='form-control'
                value={formData.name}
                onChange={onChange}
              />
            </div>
            <br />

            {/* <div className='form-group'>
              <label htmlFor='published_date'>Published Date</label>
              <input
                type='text'
                placeholder='Published Date'
                name='published_date'
                className='form-control'
                value={formData.pROFILE}
                onChange={onChange}
              /> */}
            {/* </div> */}


            <div className='form-group'>
              <label htmlFor='publisher'>phone</label>
              <input
                type='text'
                placeholder='phone'
                name='publisher'
                className='form-control'
                value={formData.phone}
                onChange={onChange}
              />
            </div>
            <br />
            </form>


    </div>
  );
};

export default UserDashboard;
