// AdminLogin.js
import React, { useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import {Card, CardContent, CardActions } from '@mui/material';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  // const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Send the admin's credentials to the backend for validation
      const response = await axios.post('http://localhost:5000/api/admin/login', { username, password });
      const { token } = response.data;

      // Store the admin's JWT token in localStorage
      localStorage.setItem('adminToken', token);

      // Redirect the admin to the Admin Dashboard
      navigate('/adminDashboard');
    } catch (error) {
      setError('Invalid credentials');
    }
  };

  return (
    <Container maxWidth="sm"> {/* Adjust maxWidth to your preference */}
    <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
      <Card style={{ width: '100%', maxWidth: '400px' }}> {/* Adjust maxWidth for the Card */}
        <CardContent>
          <Grid container direction="column" alignItems="center" spacing={2}>
            <Grid item>
              <h2>Login</h2>
              {/* You can add a subtitle or any other text here */}
            </Grid>
            <Grid item>
              <TextField
                label="Email"
                value={username}
                onChange={(event) => setUsername(event.target.value)}
                variant="outlined"
              />
            </Grid>
            <Grid item>
              <TextField
                label="Password"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
                variant="outlined"
                type="password"
              />
            </Grid>
          </Grid>
        </CardContent>
        <CardActions style={{ justifyContent: 'center', paddingBottom: '16px' }}>
          <Button onClick={handleLogin} variant="contained" color="primary">
            Login
          </Button>
        </CardActions>
      </Card>
    </Grid>
  </Container>
  )
};

export default AdminLogin;
