// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes,  Route  , Navigate  } from 'react-router-dom';
// import axios from 'axios';
import UserLogin from './Component/UserLogin';
import UserDashboard from './Component/UserDashboard';
import UserRegister from './Component/UserRegister';
import AdminLogin from './Component/Adminlogin';
import AdminDashboard from './Component/AdminDashboard';

const App = () => {
  // const [userId , setUserId] = useState("")
  const [token, setToken] = useState(localStorage.getItem('token') || '');


  useEffect(() => {
    localStorage.setItem('token', token);

  }, [token]);

  return (



    <Router>
       {/* <div> */}
      <Routes>
        <Route path='/' element = {<UserRegister/>}></Route>
        <Route
          path="/login"
          element={ <UserLogin setToken={setToken}  />}
        />
        <Route
          path="/dashboard"
          element={token ? <UserDashboard  _token={token}  /> : <Navigate to="/login" />}
        />
        <Route
        path ="/adminlogin" element = {<AdminLogin/>}></Route>

        <Route path="/editdata/:id" element={<UserDashboard />} />


      <Route
        path ="/adminDashboard" element = {<AdminDashboard/>}></Route>


      </Routes>
      {/* <div> */}
    </Router>

  );
};

export default App;
