// AdminLogin.js
import React, { useState } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import { Card, CardContent, CardActions } from "@mui/material";
import Snackbar from "@mui/material/Snackbar";
import MuiAlert from "@mui/material/Alert";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import HypervergeLogo from './Hyperverge logo.png'


const AdminLogin = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [openSnackbar, setOpenSnackbar] = useState(false);

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:5000/api/admin/login",
        { username, password }
      );
      const { token } = response.data;

      localStorage.setItem("adminToken", token);
      setError("");
      setSuccess("Login successful!");
      setOpenSnackbar(true);


      navigate("/adminDashboard");
    } catch (error) {
      setError("Invalid login credentials");
      setSuccess("");
      setOpenSnackbar(true);
      console.error("Error during request:", error.message);
    }
  };
  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <Container maxWidth="md">
    <Grid container justifyContent="center" alignItems="center" style={{ height: "100vh" }}>
      {/* Left side (Company name and logo) */}
      <Grid item xs={12} md={6} style={{ display: "flex", justifyContent: "center", alignItems: "center" }}>
        <div>
          <img src={HypervergeLogo} alt="Hyperverge Logo" style={{ width: "500px", paddingRight:"150px" }} />
          {/* <h1 style={{ fontSize: "24px", marginTop: "16px" }}>Hyperverge</h1> */}
        </div>
      </Grid>
      {/* Right side (User registration form) */}
      <Grid item xs={12} md={6}>
        <Card style={{ width: "100%", textAlign: "center", marginLeft:"180px" }}>
          <CardContent>
            <Grid container direction="column" spacing={2}>
              <Grid item>


                <h2> AdminLogin</h2>
              </Grid>
              <Grid item>
                <TextField
                  label="username"
                  value={username}
                  onChange={(event) => setUsername(event.target.value)}
                  variant="outlined"
                />
              </Grid>
              <Grid item>
                <TextField
                  label="Password"
                  value={password}
                  onChange={(event) => setPassword(event.target.value)}
                  variant="outlined"
                  type="password"
                />
              </Grid>
              </Grid>
            </CardContent>
            <CardActions style={{ justifyContent: "center" }}>
              <Button onClick={handleLogin} variant="contained" color="primary" fullWidth>
                Login
              </Button>
            </CardActions>
            </Card>

        <Snackbar
          open={openSnackbar}
          autoHideDuration={3000}
          onClose={handleCloseSnackbar}
        >
          <MuiAlert
            elevation={6}
            variant="filled"
            onClose={handleCloseSnackbar}
            severity={error ? "error" : "success"}
          >
            {error || success}
          </MuiAlert>
        </Snackbar>
      </Grid>
      </Grid>
    </Container>

  );
};

export default AdminLogin;
