import React, { useState, useEffect } from 'react';
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
// import Grid from "@mui/material/Grid";
import axios from "axios";
// import { Avatar } from "@mui/material";
import Container from "@mui/material/Container";
// import Box from "@mui/material/Box";
// import CircularProgress from "@mui/material/CircularProgress";

// import { Button } from '@mui/base';
import Button from "@mui/material/Button";
import {  useNavigate } from 'react-router-dom';
// import './App.css';
// import axios from 'axios';

function Dashboard() {

  const [users, setUsers] = useState([]);
  // const [loading, setLoading] = useState(true);
  const [selectedUser,setSelectedUsers]=useState(null)
  const [editedName, setEditedName] = useState('');
  const [editedEmail, setEditedEmail] = useState('');
  const [editedAddress, setEditedAddress] = useState('');
  const [editedPhone, setEditedPhone] = useState('');
  // const navigate = useNavigate()
  console.log("users",users)
  // const [inviteLink, setInviteLink] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/user/all");
      setUsers(response.data);
      // setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  const handleSaveEditedUser = async () => {
    try {
      const updatedUser = {
        ...selectedUser,
        name: editedName,
        email: editedEmail,
        address: editedAddress,
        phone: editedPhone
      };

      await axios.put(`http://localhost:5000/api/user/${selectedUser._id}`, updatedUser);
      fetchUsers(); // Refresh the user list
      setSelectedUsers(null); // Clear the selected user
    } catch (err) {
      console.error(err);
    }
  };




  return (
    <Container maxWidth="md">
    {/* ... Rest of your JSX ... */}

    {selectedUser && (
      <Card>
        <CardContent>
          <Typography variant="h6" align="center" gutterBottom>
            Edit User: {selectedUser.name}
          </Typography>
          <form>
            <label>Name:</label>
            <input
              type="text"
              value={editedName}
              onChange={(e) => setEditedName(e.target.value)}
            />
            <label>Email:</label>
            <input
              type="email"
              value={editedEmail}
              onChange={(e) => setEditedEmail(e.target.value)}
            />
            <label>Address:</label>
            <input
              type="text"
              value={editedAddress}
              onChange={(e) => setEditedAddress(e.target.value)}
            />
            <label>Phone:</label>
            <input
              type="text"
              value={editedPhone}
              onChange={(e) => setEditedPhone(e.target.value)}
            />
            <Button variant="contained" color="primary" onClick={handleSaveEditedUser}>
              Save
            </Button>
          </form>
        </CardContent>
      </Card>
    )}
  </Container>
);


}

export default Dashboard;
