// components/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import Avatar from "@mui/material/Avatar";
import TextField from "@mui/material/TextField";
import { useParams } from "react-router-dom";

const UserDashboard = ({ token  }) => {
 const [userData , setUserData ]= useState({})
 const {id} = useParams()
 useEffect(()=>{
  const fetchUserDetails =async()=>{
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${id}`)
      console.log(response.data)
      setUserData(response.data)
    } 
    catch (error) {
      console.log('Error from userData')

    }
  }
   fetchUserDetails()  
 },[id])





  return (
    <div>
      <h2>Welcome to Your Dashboard</h2>
      <Avatar
        src={userData.profile}
        alt={userData.name}
        sx={{ width: 156, height: 156 }}
      />

      <TextField
        id="outlined-controlled-email"
        name="email"
        label="Email"
        value={userData.email || ""}
        // onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-password"
        name="password"
        label="Password"
        value={userData.password || ""}
        // onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-name"
        name="name"
        label="Name"
        value={userData.name || ""}
        // onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-address"
        name="address"
        label="Address"
        value={userData.address || ""}
        // onChange={handleInputChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-phone"
        name="phone"
        label="Phone"
        value={userData.phone || ""}
        // onChange={handleInputChange}
        fullWidth
      />
      {/* <button onClick={handleSubmit}>Update</button> */}
    </div>
  );
};

export default UserDashboard;
