import React, { useState, useEffect } from 'react';
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import axios from "axios";
// import { Avatar } from "@mui/material";
import Container from "@mui/material/Container";
import Box from "@mui/material/Box";
import CircularProgress from "@mui/material/CircularProgress";
// import { Button } from '@mui/base';
import Button from "@mui/material/Button";
import {  useNavigate } from 'react-router-dom';
// import './App.css';
// import axios from 'axios';

function Dashboard() {

  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser,setSelectedUsers]=useState(null)
  const [editedName, setEditedName] = useState('');
  const [editedEmail, setEditedEmail] = useState('');
  const [editedAddress, setEditedAddress] = useState('');
  const [editedPhone, setEditedPhone] = useState('');
  const navigate = useNavigate()
  console.log("users",users)
  // const [inviteLink, setInviteLink] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/user/all");
      setUsers(response.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  const handleSaveEditedUser = async () => {
    try {
      const updatedUser = {
        ...selectedUser,
        name: editedName,
        email: editedEmail,
        address: editedAddress,
        phone: editedPhone
      };

      await axios.put(`http://localhost:5000/api/user/${selectedUser._id}`, updatedUser);
      fetchUsers(); // Refresh the user list
      setSelectedUsers(null); // Clear the selected user
    } catch (err) {
      console.error(err);
    }
  };




  return (
    <Container maxWidth="md">
    <Box mt={4}>
      <Grid container spacing={4} justifyContent="center">
        {loading ? (
          <CircularProgress />
        ) : (
          users.map((user) => (
            <Grid key={user._id} item xs={12} sm={6} md={4} lg={3}>
              {/* {selectedUser && */}
              <Card elevation={3}>
                <CardContent>
                  {/* <Avatar
                    src={user.profile}
                    alt={`${user.name}'s profile`}
                    sx={{ width: 80, height: 80, margin: "0 auto" }}
                  /> */}
                  <Typography variant="h6" align="center" gutterBottom>
                    {user.name}
                  </Typography>
                  {/* <Typography align="center">id:{user._id}</Typography> */}
                  <Typography color="textSecondary" align="center">
                    Email: {user.email}
                  </Typography>
                  <Typography color="textSecondary" align="center">
                    Address: {user.address}
                  </Typography>
                  <Typography color="textSecondary" align="center">
                    Phone: {user.phone}
                  </Typography>

                  <Box mt={2} textAlign="center">
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => handleUpdateUser(user._id)}
                    >
                      update
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Box>
    {selectedUser && (
        <Card>
          <CardContent>
            <Typography variant="h6" align="center" gutterBottom>
              Edit User: {selectedUser.name}
            </Typography>
            {/* Create an edit form here to allow editing */}
          </CardContent>
        </Card>
      )}
  </Container>
  );



}

export default Dashboard;
