// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes,  Route    } from 'react-router-dom';
// import axios from 'axios';
import UserLogin from './Component/UserLogin';
import Dashboard from './Component/Dashboard';
import UserRegister from './Component/UserRegister';
import AdminLogin from './Component/Adminlogin';
import AdminDashboard from './Component/AdminDashboard';
// import EditUser from './Component/EditUser.js';

const App = (props) => {
  const user = props.user
  // const [userId , setUserId] = useState("")
  const [token, setToken] = useState(localStorage.getItem('token') || '');


  useEffect(() => {
    localStorage.setItem('token', token);

  }, [token]);

  return (



    <Router>
       {/* <div> */}
      <Routes>
        <Route path='/' element = {<UserRegister/>}></Route>
        <Route
          path="/login"
          element={ <UserLogin setToken={setToken}  />}
        />


        <Route
        path ="/adminlogin" element = {<AdminLogin/>}></Route>

        <Route path="/dashboard" element={<Dashboard />} ></Route>


      <Route
        path ="/adminDashboard" element = {<AdminDashboard/>}></Route>

      </Routes>
      {/* <div> */}
    </Router>

  );
};

export default App;
