import React, { useState } from 'react';
import { Card, CardContent, TextField, Button, Typography , Grid , Box} from '@mui/material';

import axios from 'axios'; 

function AdminGenerateLink() {
  const [userId, setUserId] = useState('');
  const [registrationToken, setRegistrationToken] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleGenerateLink = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/admin/generate-link', { userId });
      setRegistrationToken(response.data.registrationToken);
      setErrorMessage('');
    } catch (error) {
      setRegistrationToken('');
      setErrorMessage(error.response.data.message);
    }
  };

  return (
    <Grid container justifyContent="center" alignItems="center" height="100vh">
    <Grid item xs={12} md={6}>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        height="100%"
      >
                  <div>
            <img
              src={HypervergeLogo}
              alt="Hyperverge Logo"
              style={{ width: "500px", paddingRight: "150px" }}
            />
          </div>
        {/* Replace with your HyperVerge logo */}

      </Box>
    </Grid>
    <Grid item xs={12} md={6}>
      <Grid
        container
        spacing={2}
        justifyContent="center"
        alignItems="center"
        height="100%"
      >
        <Grid item>
            <Card>
              <CardContent>
                <Typography variant="h5">Generate Registration Link</Typography>
                <TextField
                  label="User ID"
                  variant="outlined"
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  fullWidth
                  margin="normal"
                />
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleGenerateLink}
                >
                  Generate Link
                </Button>
                {registrationToken && (
                  <div>
                    <Typography variant="body1" color="textPrimary">
                      Generated Registration Token: {registrationToken}
                    </Typography>
                  </div>
                )}
                {errorMessage && (
                  <div>
                    <Typography variant="body1" color="error">
                      Error: {errorMessage}
                    </Typography>
                  </div>
                )}
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Grid>
    </Grid>











  );
}

export default AdminGenerateLink;
