// EditProfile.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';

import axios from 'axios';

function EditProfile( {folderName}) {
  // const { name = '', email = '', address = '', phone = '' } = 
  const { userId } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  console.log("user",user)
  const [editedUser, setEditedUser] = useState({
    name:"",
    email:"",
    phone:"",
    profile:""
  });
  const [selectedImage, setSelectedImage] = useState(null);  
  const [imageUrl, setImageUrl] = useState(""); 
  const [fileId, setFileId] = useState(null);  
  console.log("fileId" , fileId)

  const handleImageUpload = (event) => {
    setSelectedImage(event.target.files[0]);
  };
  const handleClick = () => {
    console.log("clicked",)
    if (!selectedImage) {
      return;
    }
    const data = new FormData();
    console.log(data);
    console.log("namee", folderName);
    data.append("file", selectedImage);

    data.append("upload_preset", "Image_upload");
    data.append("cloud_name", "dvib3dpad");
    data.append("folder", folderName);

    fetch("https://api.cloudinary.com/v1_1/dvib3dpad/image/upload", {
      method: "post",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setImageUrl(data.secure_url);
        setFileId(data.fileId)

        console.log(data.secure_url);
      })
      .catch((err) => {
        console.log("error");
      });
  };

  const isUserAuthenticated = true;




  useEffect(() => {
    fetchUser();
    handleClick()
  }, []);

  const fetchUser = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${userId}`);
      setUser(response.data);
      setEditedUser(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSave = async () => {
    try {
      if (selectedImage) {
        await handleClick(); // Upload image to Cloudinary
        editedUser.profile = imageUrl; // Add the image URL
      }
      await axios.put(`http://localhost:5000/api/user/${userId}`, editedUser);
      navigate('/dashboard');
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Container maxWidth="sm">
    <Card>
      <CardContent>
        <Typography variant="h5" align="center" gutterBottom>
          Edit Profile
        </Typography>
        <form>
          <TextField
            label="Name"
            fullWidth
            value={editedUser.name}
            onChange={(e) => setEditedUser({ ...editedUser, name: e.target.value })}
            margin="normal"
          />
          <TextField
            label="Email"
            fullWidth
            value={editedUser.email}
            onChange={(e) => setEditedUser({ ...editedUser, email: e.target.value })}
            margin="normal"
          />
          {/* Add other fields */}

          <TextField
            label="Phone"
            fullWidth
            value={editedUser.phone}
            onChange={(e) => setEditedUser({ ...editedUser, phone: e.target.value })}
            margin="normal"

          />
          <input
                type="file"
                className="form-control mb-3"
                onChange={handleImageUpload}
              /> 
              <br/><br/>
          <Button variant="contained" color="primary" onClick={handleSave}>
            Save
          </Button>
        </form>
      </CardContent>
    </Card>
  </Container>

  )
}

export default EditProfile;
