// components/Dashboard.js
import React ,{useState, useEffect} from 'react';
import axios from 'axios'


const UserDashboard = ({token ,_id}) => {
  const [profileData, setProfileData] = useState({});
  useEffect(() => {
    const fetchUpdateData = async () => {
      console.log("fetching:", profileData);
      try {
        const response = await axios.get(
          `http://localhost:5000/api/profile/${profileData._id}`
        );
        const data = response.data;
        console.log("fecthing the editData", data);
        // setExitData(data);


      } catch (error) {
        console.error(error);

      }
    };

    fetchUpdateData();
  }, [_id]);
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div>
      <h2>Welcome to Your Dashboard</h2>
      <TextField
                  id="outlined-controlled"
                  name="email"
                  label="Email"
                  value={email}
                  onChange={onChange}
                  fullWidth
                />

                <TextField
                  id="outlined-controlled"
                  name="password"
                  label="Password"
                  value={password}
                  onChange={onChange}
                  fullWidth
                />

                <TextField
                  id="outlined-controlled"
                  name="name"
                  label="Name"
                  value={name}
                  onChange={onChange}
                  fullWidth
                />

                <TextField
                  id="outlined-controlled"
                  name="address"
                  label="Address"
                  value={address}
                  onChange={onChange}
                  fullWidth
                />

                <TextField
                  id="outlined-controlled"
                  name="phone"
                  label="Phone"
                  value={phone}
                  onChange={onChange}
                  fullWidth
                />

    </div>
  );
};

export default UserDashboard;
