// ... (other imports)
import Button from "@mui/material/Button";
// import TextField from "@mui/material/TextField";
// import {Link} from 'react-router-dom'
// import axios from "axios";
// import { useNavigate } from "react-router-dom";
import Container from '@mui/material/Container';
// import Snackbar from "@mui/material/Snackbar";
// import MuiAlert from "@mui/material/Alert";
import { Card, CardContent } from "@mui/material";
import Box from '@mui/material/Box'
import React ,{useState, useEffect}from 'react'
import Typography from "@mui/material/Typography";
import { useNavigate } from 'react-router-dom';
import Grid from "@mui/material/Grid";
import axios from 'axios'
import EditUser from './EditUser'; // Assuming the path to your EditUser component

function Dashboard() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [editedUser, setEditedUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/user/all");
      setUsers(response.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  const handleUpdateUser = async (id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${id}`);
      setSelectedUser(response.data);
      setEditedUser(response.data); 
      navigate('/editprofile'); // Redirect to the edit profile page
    } catch (err) {
      console.error(err);
    }
  };

  const handleSaveEditedUser = async () => {
    try {
      await axios.put(`http://localhost:5000/api/user/${selectedUser._id}`, editedUser);
      fetchUsers();
      setSelectedUser(null); // Clear the selected user
      setEditedUser(null); // Clear the editedUser data
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Box mt={4}>
        <Grid container spacing={4} justifyContent="center">
          {loading ? (
            <CircularProgress />
          ) : (
            users.map((user) => (
              <Grid key={user._id} item xs={12} sm={6} md={4} lg={3}>
                <Card elevation={3}>
                  <CardContent>
                    <Typography variant="h6" align="center" gutterBottom>
                      {user.name}
                    </Typography>
                    <Typography color="textSecondary" align="center">
                      Email: {user.email}
                    </Typography>
                    <Typography color="textSecondary" align="center">
                      Address: {user.address}
                    </Typography>
                    <Typography color="textSecondary" align="center">
                      Phone: {user.phone}
                    </Typography>
                    <Box mt={2} textAlign="center">
                      <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => handleUpdateUser(user._id)}
                      >
                        Update
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))
          )}
        </Grid>
      </Box>
      {selectedUser && (
        <Card>
          <CardContent>
            <EditUser
              editedUser={editedUser}
              handleSaveEditedUser={handleSaveEditedUser}
              setEditedUser={setEditedUser}
            />
          </CardContent>
        </Card>
      )}
    </Container>
  );
}

export default Dashboard;
