import React, { useState , useEffect } from "react";
// import {useParams} from 'react-router-dom'
// import TextField from "@mui/material/TextField";
import axios from 'axios'


const ImageUpload = ({ folderName }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  // const [fileId, setFileId] = useState(null);
  // const { id } = useParams();

  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    fetchUsers();
  }, []);
  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/user/all");
      setUsers(response.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/user/${id}`);
      fetchUsers();
    } catch (err) {
      console.error(err);
    }
  };



  const handleImageUpload = (event) => {
    setSelectedImage(event.target.files[0]);
  };
  const handleClick = () => {
    console.log("clicked");
    if (!selectedImage) {
      return;
    }
    const data = new FormData();
    console.log(data);
    console.log("namee", folderName);
    data.append("file", selectedImage);

    data.append("upload_preset", "Image_upload");
    data.append("cloud_name", "dvib3dpad");
    data.append("folder", folderName);

    fetch("https://api.cloudinary.com/v1_1/dvib3dpad/image/upload", {
      method: "post",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setImageUrl(data.secure_url);
        // setFileId(data.fileId);

        console.log(data.secure_url);
      })
      .catch((err) => {
        console.log("error");
      });
  };



  const isUserAuthenticated = true;

  return (
    <>

      <div className="container">
        <h1 className="text-center">Add a post</h1>
        <div className="row justify-content-center mt-5">
          <div className="col-md-6">
            <div className="card">
              <div className="card-body">
                <input
                  type="file"
                  className="form-control mb-3"
                  onChange={handleImageUpload}
                />
                {isUserAuthenticated ? (
                  <>
                    <button
                      className="btn btn-primary"
                      disabled={!selectedImage}
                      onClick={handleClick}
                    >
                      Upload Image
                    </button>

                    {imageUrl && (
                      <div className="mt-4">
                        <h2>Uploaded Image:</h2>
                        <img
                          src={imageUrl}
                          alt="Uploaded"
                          className="img-fluid"
                        />
                      </div>
                    )}
                  </>
                ) : (
                  <p>You must be authenticated to view the image.</p>
                )}{" "}
                <br />
              </div>
            </div>
          </div>
        </div>
      </div>
      <Container maxWidth="md">
    <Box mt={4}>
      <Grid container spacing={4} justifyContent="center">
        {loading ? (
          <CircularProgress />
        ) : (
          users.map((user) => (
            <Grid key={user._id} item xs={12} sm={6} md={4} lg={3}>
              <Card elevation={3}>
                <CardContent>
                  <Avatar
                    src={user.profile}
                    alt={`${user.name}'s profile`}
                    sx={{ width: 80, height: 80, margin: "0 auto" }}
                  />
                  <Typography variant="h6" align="center" gutterBottom>
                    {user.name}
                  </Typography>
                  {/* <Typography align="center">id:{user._id}</Typography> */}
                  <Typography color="textSecondary" align="center">
                    Email: {user.email}
                  </Typography>
                  <Typography color="textSecondary" align="center">
                    Address: {user.address}
                  </Typography>
                  <Typography color="textSecondary" align="center">
                    Phone: {user.phone}
                  </Typography>

                  <Box mt={2} textAlign="center">
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => handleDeleteUser(user._id)}
                    >
                      Delete
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Box>
  </Container>
  );

       </>
  );
};

export default ImageUpload;
