import React, { useState } from 'react';
import { Card, CardContent, TextField, Button, Typography } from '@mui/material';

import axios from 'axios';

function AdminGenerateLink() {
  const [userId, setUserId] = useState('');
  const [registrationToken, setRegistrationToken] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleGenerateLink = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/admin/generate-link', { userId });
      setRegistrationToken(response.data.registrationToken);
      setErrorMessage('');
    } catch (error) {
      setRegistrationToken('');
      setErrorMessage(error.response.data.message);
    }
  };

  return (
    <Card>
    <CardContent>
      <Typography variant="h5">Generate Registration Link</Typography>
      <TextField
        label="User ID"
        variant="outlined"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}

        margin="normal"
      />
      <Button variant="contained" color="primary" onClick={handleGenerateLink}>
        Generate Link
      </Button>
      {registrationToken && (
        <div>
          <Typography variant="body1" color="textPrimary">
            Generated Registration Token: {registrationToken}
          </Typography>
        </div>
      )}
      {errorMessage && (
        <div>
          <Typography variant="body1" color="error">
            Error: {errorMessage}
          </Typography>
        </div>
      )}
    </CardContent>
  </Card>


  );
}

export default AdminGenerateLink;
