// components/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import Avatar from "@mui/material/Avatar";
import TextField from "@mui/material/TextField";
import { useNavigate, useParams } from "react-router-dom";
import Button from "@mui/material/Button";

const UserDashboard = ({ token  }) => {
  const [user,setUser]=useState({
    email:"",
    phone:"",
    address:"",
    profoile:""

  })
  const {_id} = useParams()
  const navigate = useNavigate()
  console.log("id",_id)
  useEffect(() => {
    console.log("BOOK ID", _id);
    try {
        axios.get(`http://localhost:5000/api/user/${user._id}`)
            .then((res) => {
                setUser({
                    email: res.data.email,
                    phone: res.data.phone,
                    address: res.data.address,
                    profile: res.data.profile

                });
            })
            .catch((err) => {
                console.log('Error from UpdateBookInfo');
            });
    } catch (error) {
        console.log('Error in useEffect', error);
    }
}, [_id]);
const onChange = (e) => {
  setUser({ ...user, [e.target.name]: e.target.value });
};
const onSubmit = (e) => {
  e.preventDefault();

  const data = {
    email: user.email,
    phone: user.phone,
    address: user.address,
    profile: user.profile,
  };
  axios
  .put(`http://localhost:8082/api/user/${_id}`, data)
  .then((res) => {
    navigate(`/editProfile/${user._id}`);
  })
  .catch((err) => {
    console.log('Error in UpdateBookInfo!');
  });
};





  return (
    <div>
      <h2>Welcome to Your Dashboard</h2>
      <Avatar
        src={user.profile}
        alt={user.name}
        sx={{ width: 156, height: 156 }}
      />

      <TextField
        id="outlined-controlled-email"
        name="email"
        label="Email"
        value={user.email || ""}
        onChange={onChange}
        fullWidth
      />

      <TextField
        id="outlined-controlled-address"
        name="address"
        label="Address"
        value={user.address || ""}
        onChange={onChange}
        fullWidth
      />
      <TextField
        id="outlined-controlled-phone"
        name="phone"
        label="Phone"
        value={user.phone || ""}
        onChange={onChange}
        fullWidth
      />
       <Button  variant="contained" color="primary" fullWidth>
              editProfile
            </Button>
    </div>
  );
};

export default UserDashboard;
